  `domain` varchar(10) DEFAULT '',
  `title` varchar(80) DEFAULT '',
  `name` varchar(50) DEFAULT '',
  `thumb` varchar(150) DEFAULT '',
  `logo` varchar(150) DEFAULT '',
  `slogan` varchar(255) DEFAULT '',
